	/**
	 * Obtain an IP address automatically<br>
	 * 
	 * @param evt
	 */
	private void jRadioButton1ActionPerformed(java.awt.event.ActionEvent evt) {

		if (jRadioButton1.isSelected()) {
			jTextField1.setText("");
			jTextField2.setText("");
			jTextField3.setText("");

			jTextField1.setEditable(false);
			jTextField2.setEditable(false);
			jTextField3.setEditable(false);

			isIpAuto = GlobalVar.SET_IP_VALUE_AUTO;
		} else {

			jTextField1.setEditable(true);
			jTextField2.setEditable(true);
			jTextField3.setEditable(true);

			isIpAuto = GlobalVar.SET_IP_VALUE_MANUAL;
		}
	}

	/**
	 * Obtain DNS server address automatically<br>
	 * 
	 * @param evt
	 */
	private void jRadioButton2ActionPerformed(java.awt.event.ActionEvent evt) {

		if (jRadioButton2.isSelected()) {
			jTextField4.setText("");
			jTextField5.setText("");

			jTextField4.setEditable(false);
			jTextField5.setEditable(false);

			isDnsAuto = GlobalVar.SET_DNS_VALUE_AUTO;
		} else {

			jTextField4.setEditable(true);
			jTextField5.setEditable(true);

			isDnsAuto = GlobalVar.SET_DNS_VALUE_MANUAL;
		}
	}

	/**
	 * OK Button:<br>
	 * 
	 * @param evt
	 */
	private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {

		// Check the input values START.
		// The Name of plan is required.
		if (jTextField6.getText().equals("") || jTextField6.getText() == null) {
			// TODO ALERT
			return;

		}

		// If IP Manual: IP ADDR and SUBNET MASK are required.
		if (!jRadioButton1.isSelected()) {

			if (jTextField1.getText().equals("")
					|| jTextField1.getText() == null) {
				// TODO ALERT
				return;

			}
			if (jTextField2.getText().equals("")
					|| jTextField2.getText() == null) {
				// TODO ALERT
				return;

			}
		}

		// If DNS Manual: DNS ADDR 1 is required.
		if (!jRadioButton1.isSelected()) {

			if (jTextField4.getText().equals("")
					|| jTextField4.getText() == null) {
				// TODO ALERT
				return;
			}
		}

		// Check the input values END.

		String[] planCont = { jTextField6.getText(),
				jComboBox1.getSelectedItem().toString(), jTextField1.getText(),
				jTextField2.getText(), jTextField3.getText(),
				jTextField4.getText(), jTextField5.getText(), isIpAuto,
				isDnsAuto };

		if (editFlag == GlobalVar.EDITWINDOW_EDIT_FLAG_CREATE) {
			if (process.create(planCont)) {

			} else {
				// Create Error
				// TODO ALERT 
				return;
			}
		} else {
			// IF Condition: editFlag == EDIT_FLAG_UPDATE
			// We won't handle the update ERROR, because if the source plan for
			// update is null, we will create new plan.
			// @see Map
			process.update(planCont);
		}

		process.saveConfig();

		MainWindowView.refreshJComboBox();

		this.setVisible(false);
	}

