	private void initCust() {
		process = CoreProcess.getCoreProcess();

		refreshJComboBox();

		isIpAuto = GlobalVar.SET_IP_VALUE_MANUAL;
		isDnsAuto = GlobalVar.SET_DNS_VALUE_MANUAL;
	}

	/**
	 * Set the default values for TextFields.
	 */
	public void initBeforeShow(String[] args) {

		refreshJComboBox();

		jTextField1.setEditable(true);
		jTextField2.setEditable(true);
		jTextField3.setEditable(true);
		jTextField4.setEditable(true);
		jTextField5.setEditable(true);

		if (editFlag == GlobalVar.EDITWINDOW_EDIT_FLAG_CREATE) {

			jTextField1.setText("");
			jTextField2.setText("");
			jTextField3.setText("");
			jTextField4.setText("");
			jTextField5.setText("");

			// Network Plan Name.
			jTextField6.setText("");

			//jComboBox1.setSelectedIndex(0);

			isIpAuto = GlobalVar.SET_IP_VALUE_MANUAL;
			isDnsAuto = GlobalVar.SET_DNS_VALUE_MANUAL;

			jRadioButton1.setSelected(false);
			jRadioButton2.setSelected(false);

		} else {
			// editFlag == EDIT_FLAG_UPDATE

			jTextField1.setText(args[2]);
			jTextField2.setText(args[3]);
			jTextField3.setText(args[4]);
			jTextField4.setText(args[5]);
			jTextField5.setText(args[6]);

			// Network Plan Name.
			jTextField6.setText(args[0]);

			jComboBox1.setSelectedItem(args[1]);

			if (args[7].equals(GlobalVar.SET_IP_VALUE_AUTO)) {

				isIpAuto = GlobalVar.SET_IP_VALUE_AUTO;
				jRadioButton1.setSelected(true);

				jTextField1.setEditable(false);
				jTextField2.setEditable(false);
				jTextField3.setEditable(false);
			} else {

				isIpAuto = GlobalVar.SET_IP_VALUE_MANUAL;
				jRadioButton1.setSelected(false);
			}

			if (args[8].equals(GlobalVar.SET_DNS_VALUE_AUTO)) {

				isDnsAuto = GlobalVar.SET_DNS_VALUE_AUTO;
				jRadioButton2.setSelected(true);

				jTextField4.setEditable(false);
				jTextField5.setEditable(false);
			} else {

				isDnsAuto = GlobalVar.SET_DNS_VALUE_MANUAL;
				jRadioButton2.setSelected(false);
			}
		}
	}

	public void refreshJComboBox() {

		jComboBox1.setModel(new javax.swing.DefaultComboBoxModel(process
				.getSysAdapterNames()));
	}

